import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import {
  Badge,
  Button,
  Card,
  Navbar,
  Nav,
  Table,
  Container,
  Row,
  Col,
  FormCheck,
  Form,
  Modal,
  Component,
  Dropdown,
} from "react-bootstrap";
import User from "./UserProfile";




const Users = [
  {
    id: 1,
    type: "React",
    question: "What is the correct command to create a new React project?",
    answer:"",
    status: "Processing",
    level: "Remember - Understand",
    selected: false,
  },
  {
    id: 2,
    type: "React",
    question: "What does myReactApp refer to in the following command?",
    status: "Processing",
    level: "Remember - Understand",
    selected: false,
  },
  {
    id: 3,
    type: "C#",
    question: "What command is used to start the React local development server?",
    status: "Processing",
    level: "Apply",
    selected: false,
  },
  {
    id: 4,
    type: "Java",
    question: "What is the default local host port that a React development server uses?",
    status: "Approve",
    level: "Analysis - Avaluation",
    selected: false,
  },
  {
    id: 5,
    type: ".NET",
    question: "To develop and run React code, Node.js is required.",
    status: "Approve",
    level: "Creation",
    selected: false,
  },
  {
    id: 6,
    type: "NodeJS",
    question: "What type of element will be rendered from the following code?",
    status: "Approve",
    level: "General",
    selected: false,
  },
];

class Resource extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      List: Users,
      MasterChecked: false,
      SelectedList: [],
      isOpenAdd: false,
      isOpenDelete: false,
      NumberUser: Users.length,
      selectedSkill:"empty",
      selectedLevel:"empty",
      Resource: Users,
    };
  }

  // Select/ UnSelect Table rows
  onMasterCheck(e) {
    let tempList = this.state.List;
    // Check/ UnCheck All Items
    tempList.map((user) => (user.selected = e.target.checked));

    //Update State
    this.setState({
      MasterChecked: e.target.checked,
      List: tempList,
      SelectedList: this.state.List.filter((e) => e.selected),
    });
  }

  // Update List Item's state and Master Checkbox State
  onItemCheck(e, item) {
    let tempList = this.state.List;
    tempList.map((user) => {
      if (user.id === item.id) {
        user.selected = e.target.checked;
      }
      return user;
    });

    //To Control Master Checkbox State
    const totalItems = this.state.List.length;
    const totalCheckedItems = tempList.filter((e) => e.selected).length;

    // Update State
    this.setState({
      MasterChecked: totalItems === totalCheckedItems,
      List: tempList,
      SelectedList: this.state.List.filter((e) => e.selected),
    });
  }

  // Event to get selected rows(Optional)
  getSelectedRows() {
    this.setState({
      SelectedList: this.state.List.filter((e) => e.selected),
    });
    let len = this.state.SelectedList.length
    if(len == 0)
        window.alert("Please choose questions")
    else {
        this.openModalDelete()
    }
  }

  handleSelectSkill(e){
    const skill = e.target.value;
    const level = this.state.selectedLevel
    if(skill != "SelectAll"){
        //Back to all Resource
        let tempList = this.state.Resource;
        if(level != "empty"){
           tempList = tempList.filter((ele) => ele.level == level)
        }
        //Update State
        this.setState({selectedSkill: skill, 
                        List: tempList.filter((s) => s.type == skill)
                        })
    }else{
        let tempList = this.state.Resource;
        if(this.state.selectedLevel != "empty" && this.state.selectedLevel != "SelectAll" ){
            tempList = tempList.filter((ele) => ele.level == level)
        }
            this.setState({ selectedSkill: skill,
                            selectedLevel: level, 
                            List: tempList

                })
    }
  }

  handleSelectLevel(e){
    const level = e.target.value;
    if(level != "SelectAll"){
        //Back to all Resource
        let tempList = this.state.Resource;
        if(this.state.selectedSkill != "empty"){
            tempList = tempList.filter((ele) => ele.type == this.state.selectedSkill)
        }
        //Update State
        this.setState({selectedLevel: level, 
                        List: tempList.filter((s) => s.level == level)
                        })
    }else{
        let tempList = this.state.Resource;
        if(this.state.selectedSkill != "empty" && this.state.selectedSkill != "SelectAll"){
            tempList = tempList.filter((ele) => ele.type == this.state.selectedSkill)
        }
            this.setState({ selectedlevel: level, 
                            selectedSkill: skill,
                            List: tempList
                })
    }

  }

  //Handle Add question
  openModalAdd = () => this.setState({ isOpenAdd: true });
  closeModalAdd = () => this.setState({ isOpenAdd: false });


  //Handle delete question
  openModalDelete = () => this.setState({ isOpenDelete: true });
  closeModalDelete = () => this.setState({ isOpenDelete: false });
  button = () => (console.log("Skill:", this.state.selectedSkill,", Level:", this.state.selectedLevel,"List:", this.state.List))
  render() {
    return (
      <>
      <Container fluid>
        <Row>
          <Col md="12">
            <Card className="strpied-tabled-with-hover">
              <Card.Header style={{display: 'flex', justifyContent: 'space-between'}}>
                <div className="resource-btn" style={{display: 'flex', justifyContent: 'center'}}>

                    <Form.Group className="mb-3" controlId="formBasicSelect" style={{marginRight: '10px'}}>
                        <Form.Control 
                            required 
                            type="select"
                            as="select" 
                            onChange={e => this.handleSelectSkill(e)}
                        >
                            <option value="empty">--- Select Skill ---</option>
                            <option value="React">React</option>
                            <option value="Java">Java</option>
                            <option value="NodeJS">NodeJS</option>
                            <option value=".NET">.NET</option>
                            <option value="C#">C#</option>
                            <option value="SelectAll">Select All</option>
                        </Form.Control>
                    </Form.Group>

                    <Form.Group className="mb-3" controlId="formBasicSelect" style={{}}>
                        <Form.Control as="select" onChange={e => this.handleSelectLevel(e)}>
                            <option value="empty">--- Select Level ---</option>
                            <option value="Remember - Understand">Remember - Understand</option>
                            <option value="Apply">Apply</option>
                            <option value="Analysis - Avaluation">Analysis - Avaluation</option>
                            <option value="Creation">Creation</option>
                            <option value="General">General</option>
                            <option value="SelectAll">Select All</option>
                        </Form.Control>
                    </Form.Group>
                    {/* <Button variant="primary" onClick={this.button}>Log</Button> */}
                </div>

                <div className="resource-btn">
                    <Button
                        variant="primary"
                        style={{marginRight: '10px'}}
                        onClick={this.openModalAdd}
                    >
                        Add question +
                    </Button>
                    <Button
                        variant="danger"
                        onClick={() => this.getSelectedRows()}
                    >
                        Delete  
                    </Button>
                </div>
              </Card.Header>
              <Card.Body className="table-full-width table-responsive px-0">
                <Table className="table-hover table-striped" responsive>
                  <thead style={{fontSize: '1rem'}}>
                    <tr>
                      <th className="border-0">Code</th>
                      <th className="border-0">Question</th>
                      <th className="border-0">Level</th>
                      <th className="border-0">Status</th>
                      <th className="border-0" style={{}}> 
                          <input
                              type="checkbox"
                              className="form-check-input"
                              checked={this.state.MasterChecked}
                              id="mastercheck"
                              onChange={(e) => this.onMasterCheck(e)}
                          />  
                        </th>  
                    </tr>
                  </thead>
                  <tbody>
                  {this.state.List.map((user) => (
                  <tr key={user.id} className={user.selected ? "selected" : ""}>
                    <td>{user.type.slice(0,2).toUpperCase() + user.id}</td>
                    <td>{user.question}</td>
                    <td>{user.level}</td>
                    <td>{user.status}</td>
                    <td style={{}}>
                        <input
                          type="checkbox"
                          checked={user.selected}
                          className="form-check-input"
                          id="rowcheck{user.id}"
                          onChange={(e) => this.onItemCheck(e, user)}
                        />       
                    </td>
                  </tr>
                ))} 
                  </tbody>


                </Table>
              </Card.Body>
            </Card>
          </Col>
        </Row>

      </Container>
      {/* Add */}
      <Modal show={this.state.isOpenAdd} onHide={this.closeModalAdd}>
        <Modal.Header closeButton>
          <Modal.Title>Add Question</Modal.Title>
        </Modal.Header>
        <Modal.Body>
        <Form>
            <Form.Group as={Row} className="mb-3" controlId="formPlaintextEmail">
                <Form.Label column sm="2">
                ID:
                </Form.Label>
                <Col sm="10">
                <Form.Control plaintext readOnly defaultValue={this.state.NumberUser + 1} />
                </Col>
            </Form.Group>
            <Form.Group className="mb-3" controlId="formBasicEmail">
                <Form.Control 
                    type="text" 
                    placeholder="Enter question..." 
                />
            </Form.Group>

            <Form.Group className="mb-3" controlId="formBasicSelect">
                <Form.Control as="select">
                    <option>Skill</option>
                    <option value="1">React</option>
                    <option value="2">Java</option>
                    <option value="3">NodeJS</option>
                    <option value="4">.NET</option>
                    <option value="5">C#</option>
                </Form.Control>
            </Form.Group>

            <Form.Group className="mb-3" controlId="formBasicSelect">
                <Form.Control as="select">
                    <option>Level</option>
                    <option value="1">Remember - Understand</option>
                    <option value="2">Apply</option>
                    <option value="3">Analysis - Avaluation</option>
                    <option value="4">Creation</option>
                </Form.Control>
            </Form.Group>
        </Form>
        </Modal.Body>
        <Modal.Footer style={{justifyContent: 'flex-end'}}>
          <Button 
            variant="secondary" 
            onClick={this.closeModalAdd}
            style={{marginRight: '10px'}}>
            Close
          </Button>
          <Button variant="primary" onClick={this.closeModalAdd}>
            Add
          </Button>
        </Modal.Footer>
      </Modal>

        {/* delete */}
      <Modal show={this.state.isOpenDelete} onHide={this.closeModalDelete}>
        <Modal.Header closeButton>
          <Modal.Title>Delete question</Modal.Title>
        </Modal.Header>
        <Modal.Body>
            <p>Number: {this.state.SelectedList.length}</p>
            <p>Question codes: {JSON.stringify(this.state.SelectedList.map(user => user.type.slice(0,2).toUpperCase() + user.id))}</p>
        </Modal.Body>
        <Modal.Footer >
            <p>DO YOU WANT TO DELETE ?</p>
            <div>
                <Button 
                variant="secondary" 
                onClick={this.closeModalDelete}
                style={{marginRight: '10px'}}>
                NO
                </Button>
                <Button variant="danger" onClick={this.closeModalDelete}>
                YES
                </Button>
            </div>
        </Modal.Footer>
      </Modal>
    </>
    );
  }
}

export default Resource;
