import React,{useEffect} from "react";
import axios from "axios";
import {
  useTable,
  usePagination,
  useGlobalFilter,
  useSortBy,
  useAsyncDebounce,
  useFilters,
  useRowSelect,
} from "react-table";

// A great library for fuzzy filtering/sorting items
import { Table, Input, Label } from "reactstrap";
import { Button } from "react-bootstrap";
import { Form, CloseButton, Modal, Row, Col } from "react-bootstrap";
import makeData from "./makeData";


// Define a default UI for filtering
function DefaultColumnFilter({
  column: { filterValue, preFilteredRows, setFilter },
}) {
  const count = preFilteredRows.length;

  return (
    <Form.Group className="mb-0">
      <Form.Control
        onClick={(e) => e.stopPropagation()}
        type="search"
        value={filterValue || ""}
        onChange={(e) => {
          setFilter(e.target.value || undefined); // Set undefined to remove the filter entirely
        }}
        placeholder={`Search ${count} records...`}
      ></Form.Control>
    </Form.Group>
  );
}

// This is a custom filter UI for selecting
// a unique option from a list
function SelectColumnFilter({
  column: { filterValue, setFilter, preFilteredRows, id },
}) {
  // Calculate the options for filtering
  // using the preFilteredRows
  const options = React.useMemo(() => {
    const options = new Set();
    preFilteredRows.forEach((row) => {
      options.add(row.values[id]);
    });
    return [...options.values()];
  }, [id, preFilteredRows]);

  // Render a multi-select box
  return (
    <Input
      type="select"
      value={filterValue}
      onClick={(e) => e.stopPropagation()}
      onChange={(e) => {
        setFilter(e.target.value || undefined);
      }}
    >
      <option value="">All</option>
      {options.map((option, i) => (
        <option key={i} value={option}>
          {option}
        </option>
      ))}
    </Input>
  );
}


function fuzzyTextFilterFn(rows, id, filterValue) {
  return matchSorter(rows, filterValue, { keys: [(row) => row.values[id]] });
}

// Let the table remove the filter if the string is empty
fuzzyTextFilterFn.autoRemove = (val) => !val;

//Select check box
const IndeterminateCheckbox = React.forwardRef(
    ({ indeterminate, ...rest }, ref) => {
      const defaultRef = React.useRef();
      const resolvedRef = ref || defaultRef;

      React.useEffect(() => {
        resolvedRef.current.indeterminate = indeterminate;
      }, [resolvedRef, indeterminate]);

      return (
        <>
          <input type="checkbox" ref={resolvedRef} {...rest} />
        </>
      );
    }
  );

function WrapTable({ columns, data, handleReset }) {
  const defaultColumn = React.useMemo(
    () => ({
      // Let's set up our default Filter UI
      Filter: DefaultColumnFilter,
    }),
    []
  );
  const props = useTable(
    {
      columns,
      data,
      defaultColumn,
    },
    useFilters,
    useGlobalFilter, // useGlobalFilter!
    useSortBy,
    usePagination,
    useRowSelect,
    (hooks) => {
      hooks.visibleColumns.push((columns) => [
        // Let's make a column for selection
        {
          id: "selection",
          // The header can use the table's getToggleAllRowsSelectedProps method
          // to render a checkbox
          Header: ({ getToggleAllPageRowsSelectedProps }) => (
            <div>
              <IndeterminateCheckbox {...getToggleAllPageRowsSelectedProps()} />
            </div>
          ),
          // The cell can use the individual row's getToggleRowSelectedProps method
          // to the render a checkbox
          Cell: ({ row }) => (
            <div>
              <IndeterminateCheckbox {...row.getToggleRowSelectedProps()} />
            </div>
          )
        },
        ...columns
      ]);
    }
  );
  const {
    getTableProps,
    getTableBodyProps,
    headerGroups,
    rows,
    prepareRow,
    setGlobalFilter,
    state,
    page, // Instead of using 'rows', we'll use page,
    // which has only the rows for the active page

    // The rest of these things are super handy, too ;)
    canPreviousPage,
    canNextPage,
    pageOptions,
    pageCount,
    gotoPage,
    nextPage,
    previousPage,
    setPageSize,
    selectedFlatRows,
    state: { pageIndex, pageSize, globalFilter, selectedRowIds },
  } = props;
  // console.log(props);
//   React.useEffect(() => {
//     // props.dispatch({ type: actions.resetPage })
//     console.log(globalFilter);
//   }, [globalFilter]);

    {/* Modal Delete */}    
    const [isOpenDelete, setIsOenDelete] = React.useState(false)
    const handleOpenDelete = () => {
        setIsOenDelete(true)
    }
    const hideOpenDelete = () => {
        setIsOenDelete(false);
    }

    {/* Modal Add */}
    const [isOpenAdd, setIsOenAdd] = React.useState(false)
    const handleOpenAdd = () => {
        setIsOenAdd(true)
    }
    const hideOpenAdd = () => {
        setIsOenAdd(false);
    }
  return (
    <>
    {/* Modal Add */}
    <Modal show={isOpenAdd} onHide={hideOpenAdd}>
        <Modal.Header style={{padding: '0 24px', display: 'flex', justifyContent:'space-between'}}>
          <Modal.Title style={{textTransform: 'uppercase', fontWeight: 'bold'}}>Add Question</Modal.Title>
          <CloseButton onClick={hideOpenAdd} ></CloseButton>
        </Modal.Header>
        <Modal.Body>
        <Form>
            <Form.Group as={Row} className="mb-3" controlId="formPlaintextEmail">
                <Form.Label column sm="2">
                <b>ID: </b> {data.length + 1}
                </Form.Label>
                <Col sm="10">
                <Form.Control plaintext readOnly defaultValue={data.length + 1} hidden/>
                </Col>
            </Form.Group>
            <Form.Group as={Row} className="mb-3" controlId="formBasicEmail">
                <Form.Label column sm="2">
                    <b>Question:</b>
                </Form.Label>
                <Form.Control 
                    type="text" 
                    placeholder="Enter question..." 
                />
            </Form.Group>

            <Form.Group as={Row} className="mb-3" controlId="formBasicEmail">
                <Form.Label column sm="2">
                    <b>Answer:</b>
                </Form.Label>
                <Form.Control 
                    type="text" 
                    placeholder="Enter question..." 
                />
            </Form.Group>

            <Form.Group as={Row} className="mb-3" controlId="formBasicSelect">
                <Form.Control as="select">
                    <option>Skill</option>
                    <option value="1">React</option>
                    <option value="2">Java</option>
                    <option value="3">NodeJS</option>
                    <option value="4">.NET</option>
                    <option value="5">C#</option>
                    <option value="6">General</option>
                </Form.Control>
            </Form.Group>

            <Form.Group as={Row} className="mb-3" controlId="formBasicSelect">
                <Form.Control as="select">
                    <option>Level</option>
                    <option value="1">Remember - Understand</option>
                    <option value="2">Apply</option>
                    <option value="3">Analysis - Avaluation</option>
                    <option value="4">Creation</option>
                </Form.Control>
            </Form.Group>

            <Form.Group as={Row} className="mb-3" controlId="formPlaintextEmail">
                <Form.Label column sm="2">
                Status:
                </Form.Label>
                <Col sm="10">
                <Form.Control plaintext readOnly defaultValue={"Proccessing"} />
                </Col>
            </Form.Group>
        </Form>
        </Modal.Body>
        <Modal.Footer style={{justifyContent: 'flex-end'}}>
          <Button 
            variant="secondary" 
            onClick={hideOpenAdd}
            style={{marginRight: '10px'}}
            className="btn-fill">
            Close
          </Button>
          <Button 
            variant="primary" 
            className="btn-fill"
            onClick={hideOpenAdd}
            style={{paddingLeft:'22px', paddingRight: '22px'}}
            >
            Add
          </Button>
        </Modal.Footer>
      </Modal>

    {/* Modal Delete */}
     <Modal show={isOpenDelete} onHide={hideOpenDelete}>
        <Modal.Header style={{padding: '0 24px', display: 'flex', justifyContent:'space-between'}}>
          <Modal.Title style={{textTransform: 'uppercase', fontWeight: 'bold'}}>Delete question</Modal.Title>
          <CloseButton onClick={hideOpenDelete} ></CloseButton>
        </Modal.Header>
        <Modal.Body >
            <p><b>Number question: </b>{selectedFlatRows.length} </p>
            <p style={{fontWeight: 'bold'}}>Question codes: </p>
            <div style={{maxHeight:'250px', overflowY: 'scroll', border: '1px solid #ece9ea'}}>  
              <ol>{selectedFlatRows.map(a=>(<li key={a.original.id}>{a.original.skill.slice(0,2).toUpperCase()+a.original.id}</li>))}</ol>
             </div>

        </Modal.Body>
        <Modal.Footer style={{display: 'flex', flexDirection: 'column'}}>
            <p style={{fontWeight: 'bold'}}>DO YOU WANT TO DELETE ?</p>
            <div>
                <Button 
                    variant="danger" 
                    onClick={hideOpenDelete}
                    style={{marginRight: '10px'}}
                    className="btn-fill"
                >
                    YES
                </Button>

                <Button 
                    variant="secondary" 
                    onClick={hideOpenDelete}
                    style={{paddingLeft:'20px', paddingRight: '20px'}}
                    className="btn-fill"
                >
                    NO
                </Button>
            </div>
        </Modal.Footer>
      </Modal>
      <div className="d-flex align-items-center justify-content-end pb-3" >
        {/* <div className="mr-3">
          <Label className="mb-0">Search: </Label>
        </div>
        <div className="mr-3">
          <Input
            type="search"
            value={globalFilter || ""}
            onClick={(e) => setGlobalFilter(e.target.value)}
          />
        </div> */}
        {/* <Button
          className="btn-fill"
          style={{ minWidth: "140px" }}
          onClick={handleReset}
        >
          Get Resource
        </Button> */}

        <Button
          className="btn-fill"
          variant="primary"
          style={{ minWidth: "140px", margin: '0 10px' }}
          onClick={handleOpenAdd}
        >
          Add question +
        </Button>

        <Button
          className="btn-fill"
          variant="danger" 
          style={{ minWidth: "140px" }}
          onClick={handleOpenDelete}
        >
          Delete {selectedFlatRows.length} questions
        </Button>
      </div>
     {/* <p>selectRow: {selectedFlatRows.length}</p> */}


      <style>{`
      td,tr,th{
        text-align: center;
      }
      `}</style>
      <Table bordered striped hover {...getTableProps()}>
        <thead>
          {headerGroups.map((headerGroup) => (
            <tr {...headerGroup.getHeaderGroupProps()}>
              {headerGroup.headers.map((column) => (
                <th {...column.getHeaderProps(column.getSortByToggleProps())}>
                  <div
                    className="heading"
                    style={{
                      fontSize: "16px",
                      fontWeight: "600",
                      display: "flex",
                      justifyContent: "center",
                    }}
                  >
                    {column.render("Header")}
                    <span>
                      {column.isSorted
                        ? column.isSortedDesc
                          ? " 🔽"
                          : " 🔼"
                        : ""}
                    </span>
                  </div>
                  {/* Render the columns filter UI */}

                  <div>{column.canFilter ? column.render("Filter") : null}</div>
                </th>
              ))}
            </tr>
          ))}
        </thead>
        <tbody {...getTableBodyProps()}>
          {page.map((row, i) => {
            prepareRow(row);
            return (
              <tr {...row.getRowProps()}>
                {row.cells.map((cell) => {
                  return (
                    <td {...cell.getCellProps()}>{cell.render("Cell")}</td>
                  );
                })}
              </tr>
            );
          })}
        </tbody>
      </Table>
      <div className="pagination d-flex align-items-center justify-content-center">
        <Button
          style={{marginRight: '10px'}}
          className="btn-fill mr-3"
          onClick={() => gotoPage(0)}
          disabled={!canPreviousPage}
        >
          {"<<"}
        </Button>
        <Button
          style={{marginRight: '10px'}}
          className="btn-fill mr-3"
          onClick={() => previousPage()}
          disabled={!canPreviousPage}
        >
          {"<"}
        </Button>
        <Button
          style={{marginRight: '10px'}}
          className="btn-fill mr-3"
          onClick={() => nextPage()}
          disabled={!canNextPage}
        >
          {">"}
        </Button>
        <Button
          style={{marginRight: '10px'}}
          className="btn-fill mr-5"
          onClick={() => gotoPage(pageCount - 1)}
          disabled={!canNextPage}
        >
          {">>"}
        </Button>
        <span className="mr-3 ml-5" style={{marginRight: '10px'}}>
          Page
          <strong>
            {pageIndex + 1} of {pageOptions.length}
          </strong>
        </span>
        <span className="mr-3" style={{marginRight: '10px'}}> Go to page: </span>
        <Input
          type="number"
          className="mr-3"
          defaultValue={pageIndex + 1}
          onChange={(e) => {
            const page = e.target.value ? Number(e.target.value) - 1 : 0;
            gotoPage(page);
          }}
          style={{ width: "100px", marginRight: '10px' }}
        />
        <div className="">
          <Input
            type="select"
            value={pageSize}
            onChange={(e) => {
              setPageSize(Number(e.target.value));
            }}
          >
            {[10, 20, 30, 40, 50].map((pageSize) => (
              <option key={pageSize} value={pageSize}>
                Show {pageSize}
              </option>
            ))}
          </Input>
        </div>
      </div>
      <br />
    </>
  );
}

