import React,{useEffect} from "react";
import WrapTable from "./WrapTable";
import ModalDetail from "./Modal/ModalDetail";
import ModalAdd from "./Modal/ModalAdd";
import ModalEdit from "./Modal/ModalEdit";
import ModalDeleteRow from "./Modal/ModalDeleteRow";
import ModalDeleteSelected from "./Modal/ModalDeleteSelected";
import {  SelectColumnFilter  } from "./WrapTable";

// A great library for fuzzy filtering/sorting items
import { Button,Badge } from "react-bootstrap";
//icon FontAwesomeIcon
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faPenSquare, faCircleInfo, faPlus, faFileLines, faTrash } from '@fortawesome/free-solid-svg-icons'

function CustomTable() {
  const columns = React.useMemo(
    () => [
          {
            Header: "Code",
            Filter: () => null,
            accessor: (row) => {return "N" + row.id},
          },
          {
            Header: "Question",
            accessor: "question",
            Filter: () => null,
            // Use our custom `fuzzyText` filter on this column
          },
          {
            Header: "Skill",
            accessor: "skill",
            Filter: SelectColumnFilter,
            filter: "includes",
          },
          {
            Header: "Level",
            accessor: "level",
            Filter: SelectColumnFilter,
            filter: "includes",
          },
          {
            Header: "Status",
            accessor: "status",
            Filter: SelectColumnFilter,
            filter: "includes",
            Cell: s => (
                <Badge bg={s.value == 'Approve' ? 'success' : 'danger'}>
                    {s.value}  
                </Badge>
            )
          },
          {
            Header:"Action",
            accessor: 'action',
            Filter: () => null,
            Cell: row => (
            <div style={{display:'flex'}}>
               {/* <button onClick={e=> handleEdit(row.row.original)}>...</button> */}
               <Button 
                    onClick={()=>handleDataModal(row.row.original)}
                    style={{marginRight: '15px'}}
                    variant="info"
                >
                    <FontAwesomeIcon icon={faCircleInfo}></FontAwesomeIcon>
                </Button>
                <Button 
                    onClick={()=>handleEditModal(row.row.original)}
                    variant="primary"
                    style={{marginRight: '15px'}}
                >
                    <FontAwesomeIcon icon={faPenSquare}></FontAwesomeIcon>
                </Button>
                <Button 
                    onClick={()=>handleOpenRowDeleteModal(row.row.original)}
                    variant="danger"
                    // style={{marginRight: '15px'}}
                >
                    <FontAwesomeIcon icon={faTrash}></FontAwesomeIcon>
                </Button>
            </div>
            ),
          }
        ],
    []
  );
  //Get data
  const [data, setData] = React.useState([])

  //Store data in Modal when select in row(detail and edit question)
  const [dataModal, setDataModal] = React.useState({})

  //Modal Edit question
  const [isOpenEdit, setIsOpenEdit] = React.useState(false);
  const [id, setId] = React.useState(null)
  const [updateQuestion, setUpdateQuestion] = React.useState("")
  const [updateAnswer, setUpdateAnswer] = React.useState("")
  const [updateSkill, setUpdateSkill] = React.useState("")
  const [updateStatus, setUpdateStatus] = React.useState("")
  const [updateLevel, setUpdateLevel] = React.useState("")
  const [selectedRows, setSelectedRows] = React.useState([])

// render data
  function getResource() {
    fetch("https://62c7db500f32635590cba090.mockapi.io/resources").then((result) => {
        result.json().then((resp)=>{
            setData(resp)
        })
    })
  }

  useEffect(() => {
    getResource()
  },[])


  //Modal detail question
  const [isOpen, setIsOpen] = React.useState(false);
//Handle Modal Detail question
  const hideModal = () => {
    setIsOpen(false);
    setDataModal({})
  };
  const handleDataModal = (data) => {
    setIsOpen(true)
    setDataModal(data)
  }

  //Handle Modal Edit question
  const hideModalEdit = () => {
    setIsOpenEdit(false)
    // setId(null)
    setUpdateQuestion("")
    setUpdateAnswer("")
    setUpdateSkill("")
    setUpdateLevel("")
    setUpdateStatus("")
  }
  const handleEditModal  = (data) => {
    setIsOpenEdit(true)
    setId(data.id)
    setUpdateQuestion(data.question)
    setUpdateAnswer(data.answer)
    setUpdateSkill(data.skill)
    setUpdateLevel(data.level)
    setUpdateStatus(data.status)
  } 
  const onSubmitEdit = () => {
    let item = {
            skill: updateSkill,
            question: updateQuestion,
            answer: updateAnswer,
            status: updateStatus,
            level: updateLevel}
    fetch(`https://62c7db500f32635590cba090.mockapi.io/resources/${id}`,{
        method: 'PUT',
        headers: {
            'Accept' : 'application/json',
            'Content-Type': 'application/json'
        },
        body: JSON.stringify(item)
    }).then((result) => {
        result.json().then(()=>{
            getResource()
            setIsOpenEdit(false)
        })
    })
    setId(null)
    setUpdateQuestion("")
    setUpdateAnswer("")
    setUpdateSkill("")
    setUpdateLevel("")
    setUpdateStatus("")
  }

  {/* Modal Add */}
  const [isOpenAdd, setIsOenAdd] = React.useState(false)
  const handleOpenAdd = () => {
      setIsOenAdd(true)
      setUpdateStatus("Processing")
      const newID = ~~data[data.length - 1].id + 1;
      setId(newID)

  }
  const hideOpenAdd = () => {
      setIsOenAdd(false)
      setId(null)
      setUpdateQuestion("")
      setUpdateAnswer("")
      setUpdateSkill("")
      setUpdateLevel("")
      setUpdateStatus("")
  }

  function onSubmitAdd(){
    let item = {
        skill: updateSkill,
        question: updateQuestion,
        answer: updateAnswer,
        status: updateStatus,
        level: updateLevel}
    fetch("https://62c7db500f32635590cba090.mockapi.io/resources",{
        method: 'POST',
        headers: {
            'Accept' : 'application/json',
            'Content-Type': 'application/json'
        },
        body: JSON.stringify(item)
    }).then((result) => {
        result.json().then(()=>{
            getResource()
            setIsOenAdd(false)
        })
    })
    setId(null)
    setUpdateQuestion("")
    setUpdateAnswer("")
    setUpdateSkill("")
    setUpdateLevel("")
    setUpdateStatus("")
  }

  {/* Modal Delete */}    
  const [isOpenDelete, setIsOenDelete] = React.useState(false)
  const handleOpenDelete = () => {
      if(selectedRows.length){
          setIsOenDelete(true)
      }
      else{
          window.alert('Choose questions to delete')
      }
  }
  const hideOpenDelete = () => {
      setIsOenDelete(false)
      setId(null)
      setUpdateQuestion("")
      setUpdateAnswer("")
      setUpdateSkill("")
      setUpdateLevel("")
      setUpdateStatus("")
  }

{/* Modal row Delete */} 
   const [isOpenRowDeleteModal, setIsOpenRowDeleteModal] = React.useState(false)

   const handleOpenRowDeleteModal = (data) => {
        setIsOpenRowDeleteModal(true);
        setDataModal(data)
        setId(data.id)
   }

   const hideOpenRowDeleteModal = () =>{
        setIsOpenRowDeleteModal(false)
        setDataModal({})
        setId(null)
   }

   function onSumbitDeleteRow(){
    fetch(`https://62c7db500f32635590cba090.mockapi.io/resources/${id}`,{
        method: 'DELETE',
    }).then((result) => {
        result.json().then(()=>{
            getResource()
            setIsOpenRowDeleteModal(false)
        })
    })
    setId(null)
    setDataModal({})
   }

  return (
    <>
    {/* <pre>
        <code>
          {JSON.stringify(
            {
              selectedRows

            },
            null,
            2
          )}
        </code>
      </pre> */}

    <WrapTable columns={columns} data={data} selectedRows={selectedRows} setSelectedRows={setSelectedRows} handleOpenAdd={handleOpenAdd} handleOpenDelete={handleOpenDelete}/>


    {/* Modal Detail Question  */}
    <ModalDetail isOpen={isOpen} hideModal={hideModal} dataModal={dataModal} />

    {/* Modal Add */}
    <ModalAdd 
        isOpenAdd = {isOpenAdd} 
        hideOpenAdd = {hideOpenAdd} 
        id = {id} 
        updateSkill = {updateSkill} 
        setUpdateSkill = {setUpdateSkill}
        updateLevel = {updateLevel}
        setUpdateLevel = {setUpdateLevel}
        updateQuestion = {updateQuestion}
        setUpdateQuestion = {setUpdateQuestion}
        updateAnswer = {updateAnswer}
        setUpdateAnswer = {setUpdateAnswer}
        onSubmitAdd = {onSubmitAdd}
    />

    {/* Modal edit */}
    <ModalEdit
            isOpenEdit = {isOpenEdit}
            hideModalEdit = {hideModalEdit}
            id = {id} 
            updateStatus = {updateStatus}
            setUpdateStatus = {setUpdateStatus}
            updateSkill = {updateSkill} 
            setUpdateSkill = {setUpdateSkill}
            updateLevel = {updateLevel}
            setUpdateLevel = {setUpdateLevel}
            updateQuestion = {updateQuestion}
            setUpdateQuestion = {setUpdateQuestion}
            updateAnswer = {updateAnswer}
            setUpdateAnswer = {setUpdateAnswer}
            onSubmitEdit = {onSubmitEdit}
    />

    {/* Modal Delete Selected Questions*/}  
    <ModalDeleteSelected isOpenDelete={isOpenDelete} hideOpenDelete={hideOpenDelete} selectedRows={selectedRows}/>

    {/* Delete Each Row  */}
    <ModalDeleteRow 
        isOpenRowDeleteModal = { isOpenRowDeleteModal}
        hideOpenRowDeleteModal = {hideOpenRowDeleteModal}
        dataModal = { dataModal}
        onSumbitDeleteRow = { onSumbitDeleteRow} 
    />
    </>
  );
}

export default CustomTable;
