import React,{useEffect} from "react";
import WrapTable from "./WrapTable";
import ModalDetail from "./Modal/ModalDetail";
import {  SelectColumnFilter  } from "./WrapTable";

// A great library for fuzzy filtering/sorting items
import { Button,Badge } from "react-bootstrap";
import { Form, CloseButton, Modal, Row, Col } from "react-bootstrap";

//icon FontAwrsomeIcon
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faPenSquare, faCircleInfo, faPlus, faFileLines, faTrash } from '@fortawesome/free-solid-svg-icons'

function CustomTable() {
  const columns = React.useMemo(
    () => [
          {
            Header: "Code",
            Filter: () => null,
            accessor: (row) => {return "N" + row.id},
          },
          {
            Header: "Question",
            accessor: "question",
            // Use our custom `fuzzyText` filter on this column
          },
          {
            Header: "Skill",
            accessor: "skill",
            Filter: SelectColumnFilter,
            filter: "includes",
          },
          {
            Header: "Level",
            accessor: "level",
            Filter: SelectColumnFilter,
            filter: "includes",
          },
          {
            Header: "Status",
            accessor: "status",
            Filter: SelectColumnFilter,
            filter: "includes",
            Cell: s => (
                <Badge bg={s.value == 'Approve' ? 'success' : 'danger'}>
                    {s.value}  
                </Badge>
            )
          },
          {
            Header:"Action",
            accessor: 'action',
            Filter: () => null,
            Cell: row => (
            <div style={{display:'flex'}}>
               {/* <button onClick={e=> handleEdit(row.row.original)}>...</button> */}
               <Button 
                    onClick={()=>handleDataModal(row.row.original)}
                    style={{marginRight: '15px'}}
                    variant="info"
                >
                    <FontAwesomeIcon icon={faCircleInfo}></FontAwesomeIcon>
                </Button>
                <Button 
                    onClick={()=>handleEditModal(row.row.original)}
                    variant="primary"
                    style={{marginRight: '15px'}}
                >
                    <FontAwesomeIcon icon={faPenSquare}></FontAwesomeIcon>
                </Button>
                <Button 
                    onClick={()=>handleOpenRowDeleteModal(row.row.original)}
                    variant="danger"
                    // style={{marginRight: '15px'}}
                >
                    <FontAwesomeIcon icon={faTrash}></FontAwesomeIcon>
                </Button>
            </div>
            ),
          }
        ],
    []
  );
  //Get data
  const [data, setData] = React.useState([])

  //Store data in Modal when select in row(detail and edit question)
  const [dataModal, setDataModal] = React.useState({})

  //Modal Edit question
  const [isOpenEdit, setIsOpenEdit] = React.useState(false);
  const [id, setId] = React.useState(null)
  const [updateQuestion, setUpdateQuestion] = React.useState("")
  const [updateAnswer, setUpdateAnswer] = React.useState("")
  const [updateSkill, setUpdateSkill] = React.useState("")
  const [updateStatus, setUpdateStatus] = React.useState("")
  const [updateLevel, setUpdateLevel] = React.useState("")
  const [selectedRows, setSelectedRows] = React.useState([])

// render data
  function getResource() {
    fetch("https://62c7db500f32635590cba090.mockapi.io/resources").then((result) => {
        result.json().then((resp)=>{
            setData(resp)
        })
    })
  }

  useEffect(() => {
    getResource()
  },[])


  //Modal detail question
  const [isOpen, setIsOpen] = React.useState(false);
//Handle Modal Detail question
  const hideModal = () => {
    setIsOpen(false);
    setDataModal({})
  };
  const handleDataModal = (data) => {
    setIsOpen(true)
    setDataModal(data)
  }

  //Handle Modal Edit question
  const hideModalEdit = () => {
    setIsOpenEdit(false)
    // setId(null)
    setUpdateQuestion("")
    setUpdateAnswer("")
    setUpdateSkill("")
    setUpdateLevel("")
    setUpdateStatus("")
  }
  const handleEditModal  = (data) => {
    setIsOpenEdit(true)
    setId(data.id)
    setUpdateQuestion(data.question)
    setUpdateAnswer(data.answer)
    setUpdateSkill(data.skill)
    setUpdateLevel(data.level)
    setUpdateStatus(data.status)
  } 
  const onSubmitEdit = () => {
    let item = {
            skill: updateSkill,
            question: updateQuestion,
            answer: updateAnswer,
            status: updateStatus,
            level: updateLevel}
    fetch(`https://62c7db500f32635590cba090.mockapi.io/resources/${id}`,{
        method: 'PUT',
        headers: {
            'Accept' : 'application/json',
            'Content-Type': 'application/json'
        },
        body: JSON.stringify(item)
    }).then((result) => {
        result.json().then(()=>{
            getResource()
            setIsOpenEdit(false)
        })
    })
    setId(null)
    setUpdateQuestion("")
    setUpdateAnswer("")
    setUpdateSkill("")
    setUpdateLevel("")
    setUpdateStatus("")
  }

  {/* Modal Add */}
  const [isOpenAdd, setIsOenAdd] = React.useState(false)
  const handleOpenAdd = () => {
      setIsOenAdd(true)
      setUpdateStatus("Processing")
      const newID = ~~data[data.length - 1].id + 1;
      setId(newID)

  }
  const hideOpenAdd = () => {
      setIsOenAdd(false)
      setId(null)
      setUpdateQuestion("")
      setUpdateAnswer("")
      setUpdateSkill("")
      setUpdateLevel("")
      setUpdateStatus("")
  }

  function onSubmitAdd(){
    let item = {
        skill: updateSkill,
        question: updateQuestion,
        answer: updateAnswer,
        status: updateStatus,
        level: updateLevel}
    fetch("https://62c7db500f32635590cba090.mockapi.io/resources",{
        method: 'POST',
        headers: {
            'Accept' : 'application/json',
            'Content-Type': 'application/json'
        },
        body: JSON.stringify(item)
    }).then((result) => {
        result.json().then(()=>{
            getResource()
            setIsOenAdd(false)
        })
    })
    setId(null)
    setUpdateQuestion("")
    setUpdateAnswer("")
    setUpdateSkill("")
    setUpdateLevel("")
    setUpdateStatus("")
  }

  {/* Modal Delete */}    
  const [isOpenDelete, setIsOenDelete] = React.useState(false)
  const handleOpenDelete = () => {
      if(selectedRows.length){
          setIsOenDelete(true)
      }
      else{
          window.alert('Choose questions to delete')
      }
  }
  const hideOpenDelete = () => {
      setIsOenDelete(false)
      setId(null)
      setUpdateQuestion("")
      setUpdateAnswer("")
      setUpdateSkill("")
      setUpdateLevel("")
      setUpdateStatus("")
  }

{/* Modal row Delete */} 
   const [isOpenRowDeleteModal, setIsOpenRowDeleteModal] = React.useState(false)

   const handleOpenRowDeleteModal = (data) => {
        setIsOpenRowDeleteModal(true);
        setDataModal(data)
        setId(data.id)
   }

   const hideOpenRowDeleteModal = () =>{
        setIsOpenRowDeleteModal(false)
        setDataModal({})
        setId(null)
   }

   function onSumbitDeleteRow(){
    fetch(`https://62c7db500f32635590cba090.mockapi.io/resources/${id}`,{
        method: 'DELETE',
    }).then((result) => {
        result.json().then(()=>{
            getResource()
            setIsOpenRowDeleteModal(false)
        })
    })
    setId(null)
    setDataModal({})
   }

  return (
    <>
    {/* <pre>
        <code>
          {JSON.stringify(
            {
              selectedRows

            },
            null,
            2
          )}
        </code>
      </pre> */}

    <div className="d-flex align-items-center justify-content-end pb-3" >
        <Button
            className="btn-fill"
            variant="primary"
            style={{ minWidth: "140px", margin: '0 10px' }}
            onClick={handleOpenAdd}
        >
            <FontAwesomeIcon icon={faPlus} style={{marginRight: '5px'}}></FontAwesomeIcon>
            Add question 
        </Button>

        <Button
            className="btn-fill"
            variant="danger" 
            style={{ minWidth: "140px" }}
            onClick={handleOpenDelete}
        >
            <FontAwesomeIcon icon={faTrash} style={{marginRight: '5px'}}></FontAwesomeIcon>
            Delete {selectedRows.length} 
        </Button>
    </div>

    <WrapTable columns={columns} data={data} setSelectedRows={setSelectedRows}/>


    {/* Modal Detail Question  */}
    <Modal 
        show={isOpen} 
        onHide={hideModal}
        size="lg"
        >
        <Modal.Header style={{padding: '0 24px', display: 'flex', justifyContent:'space-between'}}>
          <Modal.Title style={{textTransform: 'uppercase', fontWeight: 'bold'}}> Detail question</Modal.Title>
          <CloseButton onClick={hideModal} ></CloseButton>
        </Modal.Header>
        <Modal.Body  style={{ border: '1px solid #ece9ea'}}>
            <p><b>ID: </b> {dataModal.id} </p>
            <p><b>Code: </b>{"N"+dataModal.id} <b> - Skill: </b>{dataModal.skill}  </p>
            <p><b>Level: </b>{dataModal.level} <b> - Status: </b>{dataModal.status}</p>
            <div style={{maxHeight:'250px', overflowY: 'scroll', border: '1px solid #ece9ea'}}>    
                <p style={{margin:'0 5px'}}><b>Question: </b><br/>{dataModal.question} </p>
                {/* <br/> */}
                <p style={{margin:'0 5px'}}><b>Answer: </b><br/>{dataModal.answer} </p>
            </div>
        </Modal.Body>
        <Modal.Footer style={{display: 'flex', justifyContent: 'flex-end',marginTop: "10px"}}>
            <div>
                <Button 
                variant="primary" 
                onClick={hideModal}
                className="btn-fill"
                >
                    OK
                </Button>
            </div>
        </Modal.Footer>
      </Modal>
    <ModalDetail />
    {/* Modal edit */}
    <Modal 
        show={isOpenEdit} 
        onHide={hideModalEdit}
        size="lg"
        >
        <Modal.Header style={{padding: '0 24px', display: 'flex', justifyContent:'space-between'}}>
          <Modal.Title style={{textTransform: 'uppercase', fontWeight: 'bold'}}>Edit Question</Modal.Title>
          <CloseButton onClick={hideModalEdit} ></CloseButton>
        </Modal.Header>
        <Modal.Body style={{ border: '1px solid #ece9ea'}}>
        <Form>
            <Form.Group as={Row} className="mb-3" controlId="formPlaintextEmail">
                <Form.Label column sm="2">
                <b>ID: </b> 
                </Form.Label>
                <Col sm="10">
                <Form.Control plaintext readOnly value={id}/>
                </Col>
            </Form.Group>


            <Form.Group as={Row} className="mb-3" controlId="formPlaintextEmail">
                <Form.Label column sm="2">
                <b>Status:</b>
                </Form.Label>
                <Col sm="10">
                    <Form.Control 
                        as="select" 
                        // defaultValue={dataModal.status}
                        value={updateStatus}
                        onChange = {e => setUpdateStatus(e.target.value)}
                    >
                        <option>{updateStatus}</option>
                        <option value="Processing">Processing</option>
                        <option value="Approve">Approve</option>
                    </Form.Control>
                </Col>
            </Form.Group>

            <Form.Group as={Row} className="mb-3" controlId="formBasicSelect">
                <Form.Label column sm="2">
                    <b>Skill:</b>
                </Form.Label>
                <Col sm="10">
                    <Form.Control 
                        as="select" 
                        // defaultValue={dataModal.skill}
                        value={updateSkill}
                        onChange = {e => setUpdateSkill(e.target.value)}
                    >
                        <option>{updateSkill}</option>
                        <option value="React">React</option>
                        <option value="Java">Java</option>
                        <option value="NodeJS">NodeJS</option>
                        <option value=".NET">.NET</option>
                        <option value="C#">C#</option>
                        <option value="General">General</option>
                    </Form.Control>
                </Col>
            </Form.Group>

            <Form.Group as={Row} className="mb-3" controlId="formBasicSelect">
                <Form.Label column sm="2">
                    <b>Level:</b>
                </Form.Label>
                <Col sm="10">
                    <Form.Control 
                        as="select" 
                        value={updateLevel}
                        onChange = {e => setUpdateLevel(e.target.value)}
                    >
                        <option>{updateLevel}</option>
                        <option value="Remember - Understand">Remember - Understand</option>
                        <option value="Apply">Apply</option>
                        <option value="Analysis - Avaluation">Analysis - Avaluation</option>
                        <option value="Creation">Creation</option>
                    </Form.Control>
                </Col>
            </Form.Group>

            <Form.Group as={Row} className="mb-3" controlId="formBasicEmail">
                <Form.Label column sm="2">
                    <b>Question:</b>
                </Form.Label>
                <Form.Control 
                    type="text" 
                    placeholder="Enter question..."
                    value={updateQuestion}
                    onChange = {e => setUpdateQuestion(e.target.value)}
                />
            </Form.Group>

            <Form.Group as={Row} className="mb-3" controlId="formBasicEmail">
                <Form.Label column sm="2">
                    <b>Answer:</b>
                </Form.Label>
                <Form.Control 
                    type="text" 
                    placeholder="Enter Answer..."
                    value={updateAnswer} 
                    onChange = {e => setUpdateAnswer(e.target.value)}
                />
            </Form.Group>


        </Form>
        </Modal.Body>
        <Modal.Footer style={{display: "flex",justifyContent: 'flex-end', marginTop: "10px"}}>
          <Button 
            variant="secondary" 
            onClick={hideModalEdit}
            style={{marginRight: '10px'}}
            className="btn-fill">
            Close
          </Button>
          <Button 
            variant="primary" 
            className="btn-fill"
            onClick={onSubmitEdit}
            style={{paddingLeft:'20px', paddingRight: '20px'}}
            >
            Save
          </Button>
        </Modal.Footer>
      </Modal>

    {/* Modal Add */}
    <Modal 
        show={isOpenAdd} 
        onHide={hideOpenAdd}
        size="lg"
        >
        <Modal.Header style={{padding: '0 24px', display: 'flex', justifyContent:'space-between'}}>
          <Modal.Title style={{textTransform: 'uppercase', fontWeight: 'bold'}}>Add Question</Modal.Title>
          <CloseButton onClick={hideOpenAdd} ></CloseButton>
        </Modal.Header>
        <Modal.Body style={{ border: '1px solid #ece9ea'}}>
        <Form>
            <Form.Group as={Row} className="mb-3" controlId="formPlaintextEmail">
                <Form.Label column sm="2">
                <b>ID: </b> 
                </Form.Label>
                <Col sm="10">
                <Form.Control plaintext readOnly defaultValue={id}/>
                </Col>
            </Form.Group>


            <Form.Group as={Row} className="mb-3" controlId="formPlaintextEmail">
                <Form.Label column sm="2">
                <b>Status:</b>
                </Form.Label>
                <Col sm="10">
                <Form.Control plaintext readOnly defaultValue={"Processing"} />
                </Col>
            </Form.Group>

            <Form.Group as={Row} className="mb-3" controlId="formBasicSelect">
                <Form.Label column sm="2">
                    <b>Skill:</b>
                </Form.Label>
                <Col sm="10">

                    <Form.Control 
                        as="select"
                        value={updateSkill}
                        onChange = {e => setUpdateSkill(e.target.value)}
                        >
                        <option>-- Select Skill --</option>
                        <option value="React">React</option>
                        <option value="Java">Java</option>
                        <option value="NodeJS">NodeJS</option>
                        <option value=".NET">.NET</option>
                        <option value="C#">C#</option>
                        <option value="General">General</option>
                    </Form.Control>
                </Col>
            </Form.Group>

            <Form.Group as={Row} className="mb-3" controlId="formBasicSelect">
                <Form.Label column sm="2">
                    <b>Level:</b>
                </Form.Label>
                <Col sm="10">
                    <Form.Control 
                        as="select"
                        value={updateLevel}
                        onChange = {e => setUpdateLevel(e.target.value)}
                        >
                        <option>-- Select Level --</option>
                        <option value="Remember - Understand">Remember - Understand</option>
                        <option value="Apply">Apply</option>
                        <option value="Analysis - Avaluation">Analysis - Avaluation</option>
                        <option value="Creation">Creation</option>
                    </Form.Control>
                </Col>
            </Form.Group>

            <Form.Group as={Row} className="mb-3" controlId="formBasicEmail">
                <Form.Label column sm="2">
                    <b>Question:</b>
                </Form.Label>
                <Form.Control 
                    type="text" 
                    placeholder="Enter question..." 
                    value={updateQuestion}
                    onChange = {e => setUpdateQuestion(e.target.value)}
                />
            </Form.Group>

            <Form.Group as={Row} className="mb-3" controlId="formBasicEmail">
                <Form.Label column sm="2">
                    <b>Answer:</b>
                </Form.Label>
                <Form.Control 
                    type="text" 
                    placeholder="Enter Answer..."
                    value={updateAnswer} 
                    onChange = {e => setUpdateAnswer(e.target.value)}
                />
            </Form.Group>


        </Form>
        </Modal.Body>
        <Modal.Footer style={{display: "flex",justifyContent: 'flex-end', marginTop: "10px"}}>
          <Button 
            variant="secondary" 
            onClick={hideOpenAdd}
            style={{marginRight: '10px'}}
            className="btn-fill">
            Close
          </Button>
          <Button 
            variant="primary" 
            className="btn-fill"
            onClick={onSubmitAdd}
            style={{paddingLeft:'22px', paddingRight: '22px'}}
            >
            Add
          </Button>
        </Modal.Footer>
      </Modal>

      {/* Modal Delete */}  
     <Modal 
        show={isOpenDelete} 
        onHide={hideOpenDelete}
        size="lg"
        >
        <Modal.Header style={{padding: '0 24px', display: 'flex', justifyContent:'space-between'}}>
          <Modal.Title style={{textTransform: 'uppercase', fontWeight: 'bold'}}>Delete question</Modal.Title>
          <CloseButton onClick={hideOpenDelete} ></CloseButton>
        </Modal.Header>
        <Modal.Body style={{ border: '1px solid #ece9ea'}}>
            <p><b>Number question: </b>{selectedRows.length} </p>
            <p style={{fontWeight: 'bold'}}>Question codes: </p>
            <div style={{maxHeight:'250px', overflowY: 'scroll', border: '1px solid #ece9ea'}}>  
              <ol>{selectedRows.map(a=>(<li key={a}>{"N"+a}</li>))}</ol>
             </div>

        </Modal.Body>
        <Modal.Footer style={{display: 'flex', flexDirection: 'column'}}>
            <p style={{fontWeight: 'bold'}}>DO YOU WANT TO DELETE ?</p>
            <div>
                <Button 
                    variant="danger" 
                    onClick={hideOpenDelete}
                    style={{marginRight: '10px'}}
                    className="btn-fill"
                >
                    YES
                </Button>

                <Button 
                    variant="secondary" 
                    onClick={hideOpenDelete}
                    style={{paddingLeft:'20px', paddingRight: '20px'}}
                    className="btn-fill"
                >
                    NO
                </Button>
            </div>
        </Modal.Footer>
      </Modal>

      {/* Delete each row  */}
      <Modal 
        show={isOpenRowDeleteModal} 
        onHide={hideOpenRowDeleteModal}
        size="lg"
        >
        <Modal.Header style={{padding: '0 24px', display: 'flex', justifyContent:'center'}}>
        <Modal.Title style={{textTransform: 'uppercase', fontWeight: 'bold'}}>Delete question</Modal.Title>
          <CloseButton onClick={hideOpenRowDeleteModal} ></CloseButton>
        </Modal.Header>
        <Modal.Body  style={{ border: '1px solid #ece9ea'}}>
            <p><b>ID: </b> {dataModal.id} </p>
            <p><b>Code: </b>{"N"+dataModal.id} <b> - Skill: </b>{dataModal.skill}  </p>
            <p><b>Level: </b>{dataModal.level} <b> - Status: </b>{dataModal.status}</p>
            <div style={{maxHeight:'250px', overflowY: 'scroll'}}>    
                <p style={{margin:'0 5px'}}><b>Question: </b><br/>{dataModal.question} </p>
                {/* <p style={{margin:'0 5px'}}><b>Answer: </b><br/>{dataModal.answer} </p> */}
            </div>
        </Modal.Body>
        <Modal.Footer style={{display: 'flex', flexDirection: 'column'}}>
            <p style={{fontWeight: 'bold'}}>DO YOU WANT TO DELETE ?</p>
            <div>
                <Button 
                    variant="danger" 
                    onClick={onSumbitDeleteRow}
                    style={{marginRight: '10px'}}
                    className="btn-fill"
                >
                    YES
                </Button>

                <Button 
                    variant="secondary" 
                    onClick={hideOpenRowDeleteModal}
                    style={{paddingLeft:'20px', paddingRight: '20px'}}
                    className="btn-fill"
                >
                    NO
                </Button>
            </div>
        </Modal.Footer>
      </Modal>
    </>
  );
}

export default CustomTable;
