import React from "react";
import { Button, Form, CloseButton, Modal, Row, Col } from "react-bootstrap";

const EditStudent = ({dataModal, editFormData, handleEditFormChange}) => {
    return(
        <div className="row" >

            <p style={{marginBottom: '0px'}}><b>Student ID: </b> </p>
            <input
                type='text'
                required='required'
                placeholder=""
                name="id"
                value={dataModal.id}
                disabled
                style={{margin: '0px 0px 7px 12px'}}
            ></input>
            <p style={{marginBottom: '0px'}}><b>Fullname: </b></p>
            <input
                type='text'
                required='required'
                placeholder="Enter fullname"
                name="fullname"
                value={editFormData.fullname}
                style={{margin: '0px 0px 7px 12px'}}
                onChange={handleEditFormChange}
            ></input>

            <p style={{marginBottom: '0px'}}><b>Birth Date: </b></p>
            <input
                type='text'
                required='required'
                placeholder="Enter birthday"
                name="birthday"
                value={editFormData.birthday}
                style={{margin: '0px 0px 7px 12px'}}
                onChange={handleEditFormChange}
            ></input>

            <p style={{marginBottom: '0px'}}><b>Signup Date: </b></p>
            <input
                type='text'
                required='required'
                placeholder="Enter signupdate"
                name="signupdate"
                value={editFormData.signupdate}
                style={{margin: '0px 0px 7px 12px'}}
                onChange={handleEditFormChange}
            ></input>
            <p style={{marginBottom: '0px'}}><b>Family contact: </b></p>
            <input
                type='text'
                required='required'
                placeholder="Enter familycontact"
                name="familycontact"
                value={editFormData.familycontact}
                style={{margin: '0px 0px 7px 12px'}}
                onChange={handleEditFormChange}
            ></input>
            <p style={{marginBottom: '0px'}}><b>Parent email: </b></p>
            <input
                type='text'
                required='required'
                placeholder="Enter parentemail"
                name="parenemail"
                value={editFormData.parenemail}
                style={{margin: '0px 0px 7px 12px'}}
                onChange={handleEditFormChange}
            ></input>
            <p style={{marginBottom: '0px'}}><b>Phone number: </b></p>
            <input
                type='text'
                required='required'
                placeholder="Enter phonenumber"
                name="phonenumber"
                value={editFormData.phonenumber}
                style={{margin: '0px 0px 7px 12px'}}
                onChange={handleEditFormChange}
            ></input>
            <p style={{marginBottom: '0px'}}><b>Student status: </b></p>












        </div>
    )
}

export default EditStudent;
