import React,{useEffect} from "react";
import WrapTable from "./WrapTable";
import ModalDetail from "./Modal/ModalDetail";
import ModalAdd from "./Modal/ModalAdd";
import ModalEdit from "./Modal/ModalEdit";
import {  SelectColumnFilter  } from "./WrapTable";

// A great library for fuzzy filtering/sorting items
import { Button,Badge } from "react-bootstrap";
import { Form, CloseButton, Modal, Row, Col } from "react-bootstrap";

//icon FontAwrsomeIcon
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faPenSquare, faCircleInfo, faPlus, faFileLines, faTrash } from '@fortawesome/free-solid-svg-icons'

function CustomTable() {
  const columns = React.useMemo(
    () => [
          {
            Header: "Code",
            Filter: () => null,
            accessor: (row) => {return "N" + row.id},
          },
          {
            Header: "Question",
            accessor: "question",
            // Use our custom `fuzzyText` filter on this column
          },
          {
            Header: "Skill",
            accessor: "skill",
            Filter: SelectColumnFilter,
            filter: "includes",
          },
          {
            Header: "Level",
            accessor: "level",
            Filter: SelectColumnFilter,
            filter: "includes",
          },
          {
            Header: "Status",
            accessor: "status",
            Filter: SelectColumnFilter,
            filter: "includes",
            Cell: s => (
                <Badge bg={s.value == 'Approve' ? 'success' : 'danger'}>
                    {s.value}  
                </Badge>
            )
          },
          {
            Header:"Action",
            accessor: 'action',
            Filter: () => null,
            Cell: row => (
            <div style={{display:'flex'}}>
               {/* <button onClick={e=> handleEdit(row.row.original)}>...</button> */}
               <Button 
                    onClick={()=>handleDataModal(row.row.original)}
                    style={{marginRight: '15px'}}
                    variant="info"
                >
                    <FontAwesomeIcon icon={faCircleInfo}></FontAwesomeIcon>
                </Button>
                <Button 
                    onClick={()=>handleEditModal(row.row.original)}
                    variant="primary"
                    style={{marginRight: '15px'}}
                >
                    <FontAwesomeIcon icon={faPenSquare}></FontAwesomeIcon>
                </Button>
                <Button 
                    onClick={()=>handleOpenRowDeleteModal(row.row.original)}
                    variant="danger"
                    // style={{marginRight: '15px'}}
                >
                    <FontAwesomeIcon icon={faTrash}></FontAwesomeIcon>
                </Button>
            </div>
            ),
          }
        ],
    []
  );
  //Get data
  const [data, setData] = React.useState([])

  //Store data in Modal when select in row(detail and edit question)
  const [dataModal, setDataModal] = React.useState({})

  //Modal Edit question
